mapper.insert:
/* bbs-type.insert */
insert into hong_bbs_type (
    bbs_type_cd
   ,bbs_nm
   ,cmnt_use_yn
   ,file_atch_yn
   ,use_yn
   ,del_yn
   ,reg_id
   ,reg_dt
   ,mdfr_id
   ,mdfr_dt
) values (
     #{bbsTypeCd}
    ,#{bbsNm}
    ,#{cmntUseYn}
    ,#{fileAtchYn}
    ,#{useYn}
    ,'N'
    ,#{regId}
    ,now()
    ,#{mdfrId}
    ,now()
)

mapper.update:
/* bbs-type.update */
update hong_bbs_type
   set bbs_nm = #{bbsNm}
      ,bbs_type_cd = #{bbsTypeCd}
      ,cmnt_use_yn = #{cmntUseYn}
      ,file_atch_yn = #{fileAtchYn}
      ,use_yn = #{useYn}
  where hong_bbs_type_uid = #{hongBbsTypeUid}

mapper.delete:
/* bbs-type.delete */
update hong_bbs_type
   set del_yn = 'Y'
 where hong_bbs_type_uid = #{hongBbsTypeUid}

mapper.update:
/* bbs-type.ynUpdate */
update hong_bbs_type
   set mdfr_id = #{mdfrId}
      ,mdfr_dt = now()

      ,cmnt_use_yn = #{ynValue}


      ,file_atch_yn = #{ynValue}


      ,use_yn = #{ynValue}

 where hong_bbs_type_uid = #{uid}
