mapper.insert:
/* board-type.insert */
insert into hong_board_type (
    board_type_cd
   ,board_name
   ,comment_yn
   ,file_atch_yn
   ,use_yn
   ,delete_yn
   ,reg_id
   ,reg_dt
   ,mdfr_id
   ,mdfr_dt
) values (
     #{boardTypeCd}
    ,#{boardName}
    ,#{commentYn}
    ,#{fileAtchYn}
    ,#{useYn}
    ,'N'
    ,#{regId}
    ,now()
    ,#{mdfrId}
    ,now()
)

mapper.update:
/* board-type.update */
update hong_board_type
   set board_name = #{boardName}
      ,board_type_cd = #{boardTypeCd}
      ,comment_yn = #{commentYn}
      ,file_atch_yn = #{fileAtchYn}
      ,use_yn = #{useYn}
  where hong_board_type_uid = #{hongBoardTypeUid}

mapper.delete:
/* board-type.delete */
update hong_board_type
   set delete_yn = 'Y'
 where hong_board_type_uid = #{hongBoardTypeUid}
