mapper.insert:
/* comment.insert */
insert into hong_cmnt (
    cmnt_cn
   ,target_type
   ,target_uid
   ,del_yn
   ,reg_id
   ,reg_dt
   ,mdfr_id
   ,mdfr_dt
) values (
    #{cmntCn}
   ,#{targetType}
   ,#{targetUid}
   ,'N'
   ,#{regId}
   ,now()
   ,#{mdfrId}
   ,now()
)

mapper.delete:
/* comment.delete */
update hong_cmnt
   set del_yn = 'Y'
 where hong_cmnt_uid = #{commentUid}

mapper.update:
/* comment.update */
update hong_cmnt
   set cmnt_cn = #{cmntCn}
      ,mdfr_id = #{mdfrId}
      ,mdfr_dt = now()
 where hong_cmnt_uid = #{commentUid}
