mapper.select:
/* report-emo.reportEmoCount */
select count(*)
  from hong_board_report_emo
 where emotion_code = #{emotionCode}

   and reg_id = #{regId}

   and board_report_uid = #{boardReportUid}

mapper.insert:
/* report-emo.insert */
insert into hong_board_report_emo (
    board_report_uid
   ,emotion_code
   ,reg_id
   ,reg_dt
) values (
    #{boardReportUid}
   ,#{emotionCode}
   ,#{regId}
   ,now()
)

mapper.delete:
/* report-emo.delete */
delete from hong_board_report_emo
 where reg_id = #{regId}
   and board_report_uid = #{boardReportUid}
   and emotion_code = #{emotionCode}
