mapper.update:
/* team-admin.changeApproval */
update hong_team
   set approval_yn = #{approvalYn}
      ,mdfr_id = #{mdfrId}
      ,mdfr_dt = now()
 where hong_team_uid = #{hongTeamUid}

/* team-admin.changeUse */
update hong_team
   set use_yn = #{useYn}
      ,mdfr_id = #{mdfrId}
      ,mdfr_dt = now()
where hong_team_uid = #{hongTeamUid}
