mapper.select:
/* chkUserEmail */
select count(*)
  from hong_user
 where user_email = #{userEmail}
   and user_uid != #{userUid}

mapper.update:
/* updateUser */
update hong_user
   set user_name = #{userName}

      ,password = #{password}
      ,pwd_last_update = now()


      ,user_email = #{userEmail}

 where user_uid = #{userUid}

/* unlock */
update hong_user
   set is_non_locked = true
      ,pwd_fail_cnt = 0
 where user_uid = #{userUid}

/* toEnable */
update hong_user
   set is_enable = true
 where user_uid = #{userUid}

/* toDisable */
update hong_user
   set is_enable = false
 where user_uid = #{userUid}
