mapper.insert:
/* HongUserInsertDto.joinUser */
insert into hong_user (
    user_id
   ,password
   ,user_name
   ,user_email
   ,role
   ,is_enable
   ,pwd_last_update
   ,last_login_date
   ,is_non_locked
   ,pwd_fail_cnt

   ,social_user_uid

) values (
    #{userId}
   ,#{password}
   ,#{userName}
   ,#{userEmail}
   ,#{role}
   ,true
   ,#{pwdLastUpdate}
   ,#{lastLoginDate}
   ,true
   ,0

   ,#{socialUserUid}

)

mapper.select:
/* HongCheckUserVo.checkUser */
select user_id
      ,pwd_fail_cnt
  from hong_user
 where user_id = #{userId}

mapper.update:
/* HongUserUpdateFailCntDto.updatePwdFailCnt */
update hong_user
   set pwd_fail_cnt = #{pwdFailCnt}

      ,is_non_locked = false

 where user_id = #{userId}

/* resetFailCnt */
update hong_user
   set pwd_fail_cnt = 0
 where user_id = #{userId}

mapper.insert:
/* HongSocialUserInsertDto.joinSocialUser */
insert into hong_social_user ( user_id )
values ( #{userId} )

mapper.select:
/* searchId */
select user_id
  from hong_user
 where user_name = #{userName}
   and user_email = #{userEmail}

mapper.update:
/* HongUserUpdatePwdDto.updateUserPwd */
update hong_user
   set password = #{password}
 where user_email = #{userEmail}
   and user_name = #{userName}
