mapper.insert:
/* HongVerifyCodeInsertDto.join */
insert into hong_verify_code (
    user_email
   ,verify_check
   ,verify_code
   ,verify_created
   ,verify_expire
) values (
    #{userEmail}
   ,#{verifyCheck}
   ,#{verifyCode}
   ,#{verifyCreated}
   ,#{verifyExpire}
)

mapper.select:
/* HongVerifyCheckDto.checkVerify */
select verify_code
  from hong_verify_code
 where user_email = #{userEmail}
   and verify_expire > now()
   and verify_check is false
 order by verify_created desc
 limit 1;

mapper.update:
/* changeVerifyCheck */
update hong_verify_code
   set verify_check = true
 where user_email = #{userEmail}

/* changeUserLastLoginDate */
update hong_user
   set last_login_date = now()
 where user_id = #{userId}
