mapper.select:
/* common-file.generateKey */
select NVL(MAX(hong_file_uid)+1, 1) as uid
  from hong_file

mapper.insert:
/* common-file.saveFile */
insert into hong_file (
   hong_file_uid
  ,file_name
  ,file_url
  ,file_id
  ,file_type
  ,file_size
  ,extension
  ,down_cnt
  ,saved
  ,delete_yn
  ,reg_id
  ,mdfr_id
  ,reg_dt
  ,mdfr_dt
) values (
   #{hongFileUid}
  ,#{fileName}
  ,#{fileUrl}
  ,#{fileId}
  ,#{fileType}
  ,#{fileSize}
  ,#{extension}
  ,0
  ,'SAVED'
  ,'N'
  ,#{regId}
  ,#{mdfrId}
  ,now()
  ,now()
)

mapper.update:
/* common-file.deleteFile */
update hong_file
set saved = #{saved}
  ,delete_yn = #{deleteYn}
  ,mdfr_id = #{mdfrId}
  ,mdfr_dt = now()
where file_url = #{fileUrl}

/* common-file.updateDownCnt */
update hong_file
   set down_cnt = down_cnt + 1
 where hong_file_uid = #{hongFileUid}
   and file_url = #{fileUrl}

mapper.insert:
/* common-file.insertLog */
insert into hong_file_log (
     hong_file_uid
    ,file_url
    ,reg_id
    ,reg_dt
) values (
     #{hongFileUid}
    ,#{fileUrl}
    ,#{regId}
    ,now()
)
